package demopac;

public class primeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//0,1,3,5,7,9,11,13.........
		
		for(int i=0; i<=10; i++)
		{
			if(i%2!=0)
			{
				System.out.println(i+" is a prime no");
			}
			else
			{
				System.out.println(i+" is not a prime no");
			}
			
			
			
			
			
			
		}
		
		
		
		
		
		
		
		
		
		
	}

}
